//////////////////////////////////////////////////////////////////

// Instancia de un servidor OPC 

//////////////////////////////////////////////////////////////////

function constructAlarmAddressSpace(server, addressSpace, eventObjects, done) {
  // server = the created node-opcua server
  // addressSpace = address space of the node-opcua server
  // eventObjects = add event variables here to hold them in memory from this script

  // internal sandbox objects are:
  // node = the compact server node,
  // coreServer = core compact server object for debug and access to NodeOPCUA
  // this.sandboxNodeContext = node context node-red
  // this.sandboxFlowContext = flow context node-red
  // this.sandboxGlobalContext = global context node-red
  // this.sandboxEnv = env variables
  // timeout and interval functions as expected from nodejs



  //Dada una etiqueta (string) que indica un tipo de datos,
  //devuelve un string con el mismo tipo, escrito de forma que lo entiendan las funciones de OPC
  //Por ejemplo, "BOOL" devuelve "Boolean"
  function convertirEtiquetaDeTipo(etiqueta) {
    switch (etiqueta) {
      case "BOOL":
        return "Boolean";
      case "DWORD":
        return "Int32";
      case "INT":
        return "Int16";
      case "REAL":
        return "Double";
      default:
        return null;
    }
  }



  //////////////////////////////////////////////////////////////////

  // CONFIGURACIONES INICIALES (no modificar)

  //////////////////////////////////////////////////////////////////

  const opcua = coreServer.choreCompact.opcua;
  // Se accede al nodo raíz del espacio de nombres
  // De este nodo raíz se "colgarán" todos los nodos de nuestro servidor
  const rootFolder = addressSpace.findNode("RootFolder");
  const namespace = addressSpace.getOwnNamespace();
  const LocalizedText = opcua.LocalizedText


  // Las variables del flow las obtendremos a partir de this.sandboxFlowContext.
  // En la variable FlowVars se encontrará ME_HORNO, nuestra variable de flow con toda la info del horno
  // Ayuda sobre los tipos de scopes en nodered: https://nodered.org/docs/user-guide/context 
  let FlowVars = this.sandboxFlowContext;


  //////////////////////////////////////////////////////////////////

  // CREACIÓN DEL DISPOSITIVO

  //////////////////////////////////////////////////////////////////


  // Creamos un nuevo nodo tipo objeto que hará las veces de dispositivo
  // Pasarela IIoT
  const device = namespace.addObject({
    organizedBy: addressSpace.rootFolder.objects, //organizedBy: objeto del que depende en la jerarquia de opcua
    browseName: "PasarelaModbusOPCUA"
  });

  //////////////////////////////////////////////////////////////////

  // AÑADIR CARPETAS

  //////////////////////////////////////////////////////////////////

  //const NOMBRES_MODULOS = ["ME_HORNO"];
  const NOMBRES_MODULOS = ["ME_TRANSPORTE", "ME_BASES", "ME_SPINNERS"];
  const objetosOPC = {};//En este objeto se guardaran todas las carpetas y variables creadas
  for (const i of NOMBRES_MODULOS) {
    objetosOPC[i] = {
      nombre: i,
      carpeta: namespace.addObject({
        organizedBy: device,
        browseName: i
      }),
      variables: {} //Objeto para guardar las variables del modulo
    }
  }

  //////////////////////////////////////////////////////////////////

  // CREACIÓN DE NODOS EN EL ADDRESS SPACE PARA LAS VARIABLES DE MEs

  //////////////////////////////////////////////////////////////////

  //Funcion get - lee el valor de una variable cualquiera del entorno
  function variableGetter(nombre_modulo, nombre_variable, tipo) {
    return new opcua.Variant({
      dataType: opcua.DataType[tipo],
      value: FlowVars.get(nombre_modulo)[nombre_variable].Value
    })
  }

  //Funcion set - escribe una variable cualquiera en un buffer de escritura en el entorno
  function variableSetter(nombre_modulo, nombre_variable, variant) {
    FlowVars.get("bufferEnvio").push({ [nombre_modulo]: { "name": nombre_variable, "value": variant.value } });
    return opcua.StatusCodes.Good;//Devuelve ack al cliente
  }

  for (const nombre_modulo of NOMBRES_MODULOS) {
    const modulo = FlowVars.get(nombre_modulo)
    for (const nombre_variable in modulo) {
      const tipo_variable = convertirEtiquetaDeTipo(modulo[nombre_variable].type)
      const descripcionVariable = modulo[nombre_variable].Descripcion
      objetosOPC[nombre_modulo]["variables"][nombre_variable] = {
        nombre: nombre_variable,
        tipo: tipo_variable
      }

      let valor = {
        get: variableGetter.bind(
          null,
          objetosOPC[nombre_modulo]["nombre"],
          objetosOPC[nombre_modulo]["variables"][nombre_variable]["nombre"],
          objetosOPC[nombre_modulo]["variables"][nombre_variable]["tipo"])
      }
      const permisos = modulo[nombre_variable].Permisos;
      if (permisos == "W") {
        valor.set = variableSetter.bind(
          null,
          objetosOPC[nombre_modulo]["nombre"],
          objetosOPC[nombre_modulo]["variables"][nombre_variable]["nombre"]
        )
      }

      objetosOPC[nombre_modulo]["variables"][nombre_variable]["nodo"] = namespace.addVariable({
        componentOf: objetosOPC[nombre_modulo]["carpeta"],
        browseName: nombre_variable,
        displayName: nombre_variable,
        description: descripcionVariable,
        dataType: tipo_variable,
        nodeId: "ns=1;s=" + nombre_modulo + "_" + nombre_variable,
        value: valor,
        eventSourceOf: objetosOPC[nombre_modulo]["carpeta"]
      })
    }
  }


  //Ejemplo historico
  //addressSpace.installHistoricalDataNode(objetosOPC["ME_HORNO"]["variables"]["Temp"]["nodo"]);
  addressSpace.installHistoricalDataNode(objetosOPC["ME_BASES"]["variables"]["A1"]["nodo"]);


  //Alarmas booleanas
