    dataType: "Boolean"
  });
  alarmaErrorNormalState.setValueFromSource({ dataType: "Boolean", value: false });

  for (const nombre_modulo in objetosOPC) {
    const modulo = objetosOPC[nombre_modulo]
    for (const nombre_variable in modulo.variables) {
      const variable = modulo.variables[nombre_variable]
      if (nombre_variable.startsWith("Error") || nombre_variable.startsWith("Alarma")) {
        const descripcionVariable = FlowVars.get(modulo.nombre)[nombre_variable].Descripcion
        const alarma = namespace.instantiateOffNormalAlarm(
          {
            componentOf: modulo.carpeta,
            conditionSource: variable.nodo,
            browseName: "alarma" + nombre_variable,
            displayName: "alarma" + nombre_variable,
            message: descripcionVariable,
            description: descripcionVariable,
            optionals: [
              'ConfirmedState', 'Confirm'
            ],
            inputNode: variable.nodo,
            normalState: alarmaErrorNormalState
          })
        eventObjects[nombre_variable] = variable.nodo
        eventObjects["alarma" + nombre_variable] = alarma
      }
    }
  }

  // Metodos para usar fases

  const metodoIniciarFase = namespace.addMethod(
    device,
    {
      browseName: "IniciarFase",
