        { name: "setpoint", description: "SP de posicion o estacion si se usan las fases del transporte", dataType: opcua.DataType.UInt32 }
      ],
      outputArguments: [
        { name: "resultado", description: "True si la fase se inicio", dataType: opcua.DataType.Boolean }
      ]
    }
  )

  metodoIniciarFase.bindMethod(
    (inputArguments, context, callback) => {
      const nombre_modulo = inputArguments[0].value;
      const numSRV = inputArguments[1].value;
      const setpoint = inputArguments[2].value;
      console.log("Metodo IniciarFase en modulo " + nombre_modulo + " y servicio " + numSRV);

      const callMethodResult = {
        statusCode: opcua.StatusCodes.Good,
        outputArguments: [{
          dataType: opcua.DataType.Boolean,
          value: false
        }]
      };

      const modulo = FlowVars.get(nombre_modulo);
      if (modulo == null) {
        console.log("Modulo " + nombre_modulo + " no se encontro en FlowVars");
        callback(null, callMethodResult);
      }
      // Si estado != 0, el modulo no esta en idle
      else if (modulo.EstadoActual.Value != 0) {
        console.log("Modulo " + nombre_modulo + " no esta en estado 0 (idle)");
        callback(null, callMethodResult);
      }
      else {
        if (nombre_modulo == "ME_TRANSPORTE") {
          // Servicio ir a posicion
          if (numSRV == 3) { FlowVars.get("bufferEnvio").push({ [nombre_modulo]: { "name": "SP_Posicion", "value": setpoint } }); }
          // Servicio ir a estacion
          if (numSRV == 4) { FlowVars.get("bufferEnvio").push({ [nombre_modulo]: { "name": "SP_Estacion", "value": setpoint } }); }
        }
        FlowVars.get("bufferEnvio").push({ [nombre_modulo]: { "name": "NumSRV", "value": numSRV } });
        FlowVars.get("bufferEnvio").push({ [nombre_modulo]: { "name": "Evento", "value": 1 } }); // Evento start
        callMethodResult.outputArguments[0].value = true;
        callback(null, callMethodResult);
      }
    }
  )

  //Metodo para comprobar si una fase se ha acabado y enviar el evento reset para devolverla a idle
  metodoCompletarFase = namespace.addMethod(
    device,
    {
      browseName: "CompletarFase",
      inputArguments: [
        { name: "modulo", description: "ME_TRANSPORTE, ME_BASES o ME_SPINNERS", dataType: opcua.DataType.String }
      ],
      outputArguments: [
        { name: "resultado", description: "True si la fase se ha completado", dataType: opcua.DataType.Boolean }
      ]
