  metodoCompletarFase.bindMethod(
    (inputArguments, context, callback) => {
      const nombre_modulo = inputArguments[0].value;
      console.log("Metodo CompletarFase en modulo " + nombre_modulo);

      const callMethodResult = {
        statusCode: opcua.StatusCodes.Good,
        outputArguments: [{
          dataType: opcua.DataType.Boolean,
