        statusCode: opcua.StatusCodes.Good,
        outputArguments: [{
          dataType: opcua.DataType.Boolean,
          value: false
        }]
      };

      const modulo = FlowVars.get(nombre_modulo);
      if (modulo == null) {
        console.log("Modulo " + nombre_modulo + " no se encontro en FlowVars");
        callback(null, callMethodResult);
      }
      else {
        FlowVars.get("bufferEnvio").push({ [nombre_modulo]: { "name": "Evento", "value": 4 } }); // Evento reset
        callMethodResult.outputArguments[0].value = true;
        callback(null, callMethodResult);
      }
    }
  )

  //Metodo para abortar todos los modulos
  metodoAbortarModulos = namespace.addMethod(
    device,
    {
      browseName: "AbortarModulos",
      inputArguments: [],
      outputArguments: [
        { name: "resultado", description: "True si el modulo se ha reseteado", dataType: opcua.DataType.Boolean }
