      for (const nombre_modulo of NOMBRES_MODULOS) {
        const modulo = FlowVars.get(nombre_modulo);
        if (modulo == null) {
          console.log("Modulo " + nombre_modulo + " no se encontro en FlowVars");
          callback(null, callMethodResult);
        }
        // Si estado == 1, el modulo esta en running
        else if (modulo.EstadoActual.Value == 1) {
          FlowVars.get("bufferEnvio").push({ [nombre_modulo]: { "name": "Evento", "value": 3 } }); // Evento abort
          callMethodResult.outputArguments[0].value = true;
          callback(null, callMethodResult);
        }
      }
    }
  )

