  //Funcion get - lee el valor de una variable cualquiera del entorno
  function variableGetter(nombre_modulo, nombre_variable, tipo) {
    return new opcua.Variant({
      dataType: opcua.DataType[tipo],
      value: FlowVars.get(nombre_modulo)[nombre_variable].Value
    })
  }

  //Funcion set - escribe una variable cualquiera en un buffer de escritura en el entorno
  function variableSetter(nombre_modulo, nombre_variable, variant) {
    FlowVars.get("bufferEnvio").push({ [nombre_modulo]: { "name": nombre_variable, "value": variant.value } });
    return opcua.StatusCodes.Good;//Devuelve ack al cliente
  }

  for (const nombre_modulo of NOMBRES_MODULOS) {
    const modulo = FlowVars.get(nombre_modulo);
    for (const nombre_variable in modulo) {
      const tipo_variable = convertirEtiquetaDeTipo(modulo[nombre_variable].type);
      objetosOPC[nombre_modulo]["variables"][nombre_variable] = {
        nombre: nombre_variable,
        tipo: tipo_variable
      }

      let valor = {
        get: variableGetter.bind(
          null,
          objetosOPC[nombre_modulo]["nombre"],
          objetosOPC[nombre_modulo]["variables"][nombre_variable]["nombre"],
          objetosOPC[nombre_modulo]["variables"][nombre_variable]["tipo"])
      }
      const permisos = modulo[nombre_variable].Permisos;
      if (permisos == "W") {
        valor.set = variableSetter.bind(
          null,
          objetosOPC[nombre_modulo]["nombre"],
          objetosOPC[nombre_modulo]["variables"][nombre_variable]["nombre"]
        )
      }

      objetosOPC[nombre_modulo]["variables"][nombre_variable]["variable"] = namespace.addVariable({
        componentOf: objetosOPC[nombre_modulo]["carpeta"],
        browseName: nombre_variable,
        dataType: tipo_variable,
        nodeId: "ns=1;s=" + nombre_variable,
        value: valor
      })
    }
